import Card from "./card"
const styles = {
    root: {
      display : "flex",
      flexDirection: "row",
      flexWrap : "wrap",
      width : "80%",
    },}
export default function ShowCace({items, deposit}) {
    const isEnabled = (item)=> {
return (item.price<=deposit)
    }
    return (
      <div style={styles.root}>
        {items.map(item => <Card key={item.name} item={item} isEnabled={isEnabled}/>)}
      </div>
    );
  }
