import { useState } from "react";
import ShowCase from "../components/showcase";
import ControlPanel from "../components/controlpanel";
import Delivery from "../components/delivery"

const styles = {
    root: {
      display: "flex",
      flexDirection: "row",
    },}


export default function Index() {
  const items = [
    { name: "Cola", type: "Cold drink", price: 130, "ok":false},
    { name: "Evian", type: "Water", price: 90, "ok":false },
    { name: "Durex", type: "Condoms", price: 600, "ok":false },
    { name: "Snickers", type: "Choco bar", price: 105, "ok":false },
    { name: "Wagon Wheels", type: "Choco cookies", price: 165, "ok":false },
    { name: "WTF, Alice?", type: "I don't know", price: 200, "ok":false },
    { name: "Fanta", type: "Cold drink", price: 130, "ok":false}
  ];
  const [deposit, setDeposit] = useState("");
  const [selected, setSelected] = useState("");
  const [purchasedItem, setpurchasedItem] = useState("");

  {items.map(item => 
    (item.price<=deposit? item.ok=true:item.ok=false))}

    const purchased = () => {
    if (items[selected-1].ok)
    setpurchasedItem(items[selected-1])
    }

  return (
    <div style={styles.root}>
      <ShowCase items={items} deposit={deposit} />
      <ControlPanel
        setDeposit={setDeposit}
        setSelected={setSelected}
        deposit={deposit}
        selected={selected}
        purchased={purchased}
      />
      <Delivery purchasedItem={purchasedItem}/>
      {console.log(purchasedItem)}
    </div>
  );
}
