const styles = {
    showcase_body: {
      width: "100%",
      height : "100%",
      position: "relative",
      padding: "2rem 1.25rem",
      border: "0.125rem solid #0e4e0f"
    },
    product_list: {
      display: "flex",
      flexWrap: "wrap",
      aligContent: "streth",
      paddingLeft: 0,
      marginTop: 0,
      marginRight: "-0.875rem",
      marginBottom: 0,
    },
  }
export default function ShowCace({items, deposit}) {
    const isEnabled = (item)=> {
return (item.price<=deposit)
    }

    return (
      <div style={styles.showcase_body}>
        <div style={styles.product_list}>
       {items.map(item => <Card key={item.name} isEnabled={isEnabled} item={item} />)}
       </div>
      </div>
    );
  }
