import ShowCase from "../components/showcase"

export default function Index() {
    const items =[{"name": "Cola", "type":"Cold drink", "price": 85}, {"name": "Evian", "type":"Water", "price": 90} ]
    const [deposit, setDeposit] = useState(0);
    const [selected, setSelected] = useState();
    return (
      <div>
        <ShowCase items={items} />
        <ControlPanel setDeposit={setDeposit} setSelected={setSelected} deposit={deposit} selected={selected} />
      </div>
    );
  }
