import { useState } from "react";

const styles = {
  root: {
    display: "flex",
    flexDirection: "column"
  }
};
export default function ControlPanel({
  setDeposit,
  // deposit,
  // setSelected,
  // selected,
  purchased
}) 

{

  const [message, setMessage]= useState("Insert banknotes...");
  const [messageTwo, setMessageTwo]= useState(".");
  const handleChangeDeposit = event => {
    if (event.key === "Enter") {
      if (event.target.value===50&100&200&500&1000)
      setDeposit(event.target.value);
      setMessage("Inserted money:"+event.target.value+"R")
      setMessageTwo("Choose product...")
    }
    else{
      setMessage("Unknown banknote!")
    }
  };

  const handleChangeSelected = event => {
    if (event.key === "Enter") {
      // setSelected(event.target.value);
      purchased(event.target.value);
      setMessageTwo("Success!")
    }
  };

  return (
    <div style={styles.root}>
      {/* <form style={styles.root}> */}
        <span>{message}</span>
        <input
          type="text"
          name="deposit"
          // value={deposit}
          onKeyPress={handleChangeDeposit}
        ></input>
        Available banknotes: 50, 100, 200, 500 or 1000 R. The machine gives
        change in 1, 2, 5 and 10 R coins.
      {/* </form> */}
      <span>{messageTwo}</span>
      <input
        type="text"
        name="deposit"
        // value={selected}
        onKeyPress={handleChangeSelected}
      ></input>
      <button onClick={purchased}>Купить</button>
    </div>
  );
}
