import { useState } from "react";
import ShowCase from "../components/showcase";
import ControlPanel from "../components/controlpanel";
import Card from "../components/card";
import Surrender from "../components/surrender"

const styles = {
  page: {
    display: "flex",
    minHeight: "100vh",
    fontSize: "1.125rem"
  },
  page_showcase: {
    display: "flex",
    flex: "1 1 auto",
    padding: "1.75rem",
    backgroundColor: "#141a0d"
  },
  panel: {
    display: "flex",
    flex: "0 0 22.5rem",
    padding: "1.75rem",
    backgroundColor: "#1a1f15",
    // owerflowY: "scroll",
  },
  panelRow:{
    display: "flex",
    flexDirection: "row" 
  }
};
const surrender = { ten: 0, five: 0, two: 0, one: 0 };
export default function Index() {
  const items = [
    { name: "Cola", type: "Cold drink", price: 130, ok: false },
    { name: "Evian", type: "Water", price: 90, ok: false },
    { name: "Durex", type: "Condoms", price: 600, ok: false },
    { name: "Snickers", type: "Choco bar", price: 105, ok: false },
    { name: "Wagon Wheels", type: "Choco cookies", price: 165, ok: false },
    { name: "WTF, Alice?", type: "I don't know", price: 200, ok: false },
    { name: "Fanta", type: "Cold drink", price: 130, ok: false }
  ];

  const [deposit, setDeposit] = useState("");
  // const [selected, setSelected] = useState("");
  const [purchasedItem, setpurchasedItem] = useState("");

  {
    items.map(item =>
      item.price <= deposit ? (item.ok = true) : (item.ok = false)
    );
  }

  const purchased = (selected) => {
    if (items[selected - 1].ok) {
      setpurchasedItem(items[selected - 1]);
      surrender.ten = ~~((deposit - items[selected - 1].price) / 10);
      surrender.five = ~~(((deposit - items[selected - 1].price) % 10) / 5);
      surrender.two = ~~((((deposit - items[selected - 1].price) % 10) % 5) / 2);
      surrender.one = ~~(
        ((((deposit - items[selected - 1].price) % 10) % 5) % 2) /
        1
      );
    }
  };

  return (
    <div style={styles.page}>
      <div style={styles.page_showcase}>
      <ShowCase  items={items} deposit={deposit} />)
      </div>
      <div style={styles.panel}>
        <ControlPanel
          setDeposit={setDeposit}
          // setSelected={setSelected}
          deposit={deposit}
          // selected={selected}
          purchased={purchased}
        />
        <div style={styles.panelRow}>
        <Card item={purchasedItem} />
        <Surrender surrender={surrender }/>
        </div>

      </div>
    </div>
  );
}
