import {useMemo} from "react"

const makeStyles = (enabled)=> ({
  root: {
    display: "flex",
    flexDirection: "column",
    width : "30%",
    backgroundColor: enabled?"#654676":"#6546",
    border: "1px solid black",
    padding: "5px",
    margin : "5px",
    height: "30%"
  },
  text:{
    margin : "5%" 
  }
})
export default function Card({item, deposit, isEnabled}) {


    const enabled = useMemo(()=> isEnabled(item),[item,isEnabled])
    const styles = useMemo(() => makeStyles(enabled),[enabled])

    return (
      <div style={styles.root}>
       <span style={styles.text}>
        {item.name}
        </span>
        <span style={styles.text}>
        {item.type}
        </span>
        <span style={styles.text}>
        {item.price}
        </span>
      </div>
    );
  }
